  styleUrls: ['./contenido.component.css']
})
export class ContenidoComponent {
  // public image?: string;
  // public Titulo: string = ""
  @Input() dataEntrante: any;
  nombre: string;
  mostrarValorEnConsola() {
  console.log(this.nombre);
}
  removeNumbers() {
  this.nombre.replace(/[^0-9]/g,'')
}
  constructor(){
    this.nombre = "";
  }
}
